"use strict";

(function () {
  setTimeout(function () {
    document.querySelector('html').style.scrollBehavior = 'smooth';
  }, 3000);
})();

var hamburgerTop = document.querySelector('.header-mobile-btn-top');
var hamburgerMid = document.querySelector('.header-mobile-btn-middle');
var hamburgerBottom = document.querySelector('.header-mobile-btn-bottom');
var menu = document.querySelector('.header__menu');
var main = document.querySelector('main');
document.querySelector('.header-mobile-btn').addEventListener('click', function () {
  hamburgerTop.classList.toggle('top-rotate');
  hamburgerMid.classList.toggle('scale');
  hamburgerBottom.classList.toggle('bottom-rotate');
  menu.classList.toggle('left-to-right-menu');
  main.classList.toggle('none');
});
var links = document.querySelectorAll('.header__link');
links.forEach(function (link) {
  link.addEventListener('click', function () {
    hamburgerTop.classList.toggle('top-rotate');
    hamburgerMid.classList.toggle('scale');
    hamburgerBottom.classList.toggle('bottom-rotate');
    menu.classList.toggle('left-to-right-menu');
    main.classList.toggle('none');
  });
});
